mapper.update:

UPDATE t_base_dept t
SET t.rgt=t.rgt+2
WHERE id=#{id, jdbcType=NUMERIC}


UPDATE t_base_dept t
SET name=#{name, jdbcType=VARCHAR}
WHERE id=#{id, jdbcType=NUMERIC}


UPDATE t_base_dept t 
SET t.lft=t.lft-2,t.rgt=t.rgt-2
WHERE id = #{id}


UPDATE t_base_dept t 
SET t.lft=t.lft+2,t.rgt=t.rgt+2
WHERE id = #{id}
