mapper.update:

UPDATE t_base_res t
SET t.rgt=t.rgt+2
WHERE id=#{id, jdbcType=NUMERIC}

UPDATE t_base_res t


        name=#{name, jdbcType=VARCHAR},


        URL=#{url, jdbcType=VARCHAR},


        is_enable=#{isEnable, jdbcType=BOOLEAN},


        update_time=#{updateTime, jdbcType=TIMESTAMP}


WHERE id=#{id, jdbcType=NUMERIC}


UPDATE t_base_res t
SET t.lft=t.lft-2,t.rgt=t.rgt-2
WHERE id = #{id}


UPDATE t_base_res t
SET t.lft=t.lft+2,t.rgt=t.rgt+2
WHERE id = #{id}
